module.exports = function ($scope, challenge) {
  $scope.challenge = challenge;
  $scope.submissions = challenge.submissions;
  $scope.state = 'New';
  $scope.save = function(state) {
    $scope.challenge.state = state;
    $scope.challenges.$add($scope.challenge);
    $state.go('submissions.list', { challenge: $scope.challenge.$id });
  }  
};
